INSERT INTO `ingredients` (`recid`, `method`, `ing1`, `ing2`, `ing3`, `ing4`, `ing5`, `ing6`, `ing7`, `ing8`, `ing9`, `ing10`, `ing11`, `ing12`, `ing13`, `ing14`, `ing15`, `ing16`, `ing17`, `ing18`, `ing19`, `ing20`, `ing21`, `ing22`, `ing23`, `ing24`) VALUES
(12261, ' Roll dough out to 8 x 10-inch size. Brush combined seasonings on dough. (Save some to brush on top later.) Arrange pepperoni on top; cover with cheese and roll up like a jelly roll. Brush remaining seasonings on top of pepperoni roll. Bake at 350&deg; for 30 minutes on a cookie sheet. Cool 20 minutes before cutting.', '1 Rhodes pizza dough', '3 Tbsp. margarine , melted', '1/4 tsp. oregano', '1/2 tsp. parsley flakes', '1/4 tsp. seasoned salt', '3/4 c. pepperoni , thin sliced', '1 3/4 c. Provolone or Mozzarella cheese , grated', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
